		if (array_key_exists($keys[1], $arr)) $this->setScdids($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setUserid($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setTaskpriority($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setTaskstring($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setTaskdesciption($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setDeleted($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setActive($arr[$keys[7]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TasksPeer::DATABASE_NAME);

		if ($this->isColumnModified(TasksPeer::TASKID)) $criteria->add(TasksPeer::TASKID, $this->taskid);
		if ($this->isColumnModified(TasksPeer::SCDIDS)) $criteria->add(TasksPeer::SCDIDS, $this->scdids);
		if ($this->isColumnModified(TasksPeer::USERID)) $criteria->add(TasksPeer::USERID, $this->userid);
		if ($this->isColumnModified(TasksPeer::TASKPRIORITY)) $criteria->add(TasksPeer::TASKPRIORITY, $this->taskpriority);
		if ($this->isColumnModified(TasksPeer::TASKSTRING)) $criteria->add(TasksPeer::TASKSTRING, $this->taskstring);
		if ($this->isColumnModified(TasksPeer::TASKDESCIPTION)) $criteria->add(TasksPeer::TASKDESCIPTION, $this->taskdesciption);
		if ($this->isColumnModified(TasksPeer::DELETED)) $criteria->add(TasksPeer::DELETED, $this->deleted);
		if ($this->isColumnModified(TasksPeer::ACTIVE)) $criteria->add(TasksPeer::ACTIVE, $this->active);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(TasksPeer::DATABASE_NAME);

		$criteria->add(TasksPeer::TASKID, $this->taskid);

		return $criteria;
	}

